export const _specs = {}

export class AbstractRef {

  constructor ({ path, spec, value }) {
    if (new.target === AbstractRef && !spec) {
      throw new TypeError('Cannot construct AbstractRef instances without a defined `spec`')
    }

    this.path  = path
    this.spec  = spec
    this.value = value
  }

  use (obj) {
    this.value = obj

    return this
  }

  get (obj = this.value, single = true) {
    const value = this.spec.follow(this.path, obj)

    if (Array.isArray(value)) {
      return single && value.length ? value[0] : value
    }

    return single ? value : [value]
  }

  one (obj = this.value) {
    return this.get(obj, true)
  }

  all (obj = this.value) {
    return this.get(obj, false)
  }

  count (obj = this.value) {
    const results = this.get(obj)

    if (Array.isArray(value)) {
      return results.length
    }

    return results ? 1 : 0
  }

  any (obj = this.value) {
    return !!this.count(obj)
  }

  set (data, obj = this.value) {
    return this.spec.update(this.path, obj, data)
  }

}

export class AbstractRefSpec {

  constructor (label, draft) {
    const methods = ['matches', 'follow', 'update']

    if (new.target === AbstractRefSpec) {
      throw new TypeError('Cannot construct AbstractRefSpec instances directly')
    }

    methods.forEach(method => {
      if (Object.is(this[method], undefined)) {
        throw new TypeError(`Must override ${method}`)
      }
    })

    this.label = label
    this.draft = draft

    _specs[label] = this
  }

  static identify(data) {
    return Object.keys(_specs).find((label) => {
      if (_specs[label].matches(data)) {
        return label
      }
    })
  }

}

export const $ = (path, value) => {
  const key  = AbstractRefSpec.identify(path)
  const spec = _specs[key]

  if (spec instanceof AbstractRefSpec) {
    return new AbstractRef({path, value, spec})
  }

  throw new TypeError(`Failed to identify path specification for ${path}`)
}

export const which = AbstractRefSpec.identify

export default { AbstractRef, AbstractRefSpec, which, $ }

import jsonPath from 'jsonpath'
import { AbstractRef, AbstractRefSpec } from './abstract'

export class PathRef extends AbstractRef {

  constructor (path, value) {
    super({ path, value, spec: new PathRefSpec() })
  }

}

export class PathRefSpec extends AbstractRefSpec {

  constructor () {
    super('json-path', 'http://goessner.net/articles/JsonPath/')
  }

  matches (rel) {
    if (!rel || typeof rel !== 'string') {
      return false
    }

    try {
      const parsed = jsonPath.parse(rel)

      return !!parsed.length
    } catch (e) {
      return false
    }
  }

  follow (rel, data) {
    if (this.matches(rel)) {
      return jsonPath.query(data, rel)
    }

    return []
  }

  update (rel, obj, data) {
    // TODO: No core support, so add it here
    console.warn('json-path does not support updating values!')
  }

}

delete new PathRefSpec() // register spec with global pool by immediately invoking it

export const path = (path, value) => new PathRef(path, value)

export default { PathRef, PathRefSpec, path }

import jsonPointer from 'jsonpointer'
import { AbstractRef, AbstractRefSpec } from './abstract'

export class PointerRef extends AbstractRef {

  constructor (path, value) {
    super({ path, value, spec: new PointerRefSpec() })
  }

}

export class PointerRefSpec extends AbstractRefSpec {

  constructor () {
    super('json-pointer', 'http://tools.ietf.org/html/draft-ietf-appsawg-json-pointer-08')
  }

  matches (rel) {
    if (!rel || typeof rel !== 'string') {
      return false
    }

    try {
      return typeof jsonPointer.compile(rel) === 'object'
    } catch (e) {
      return false
    }
  }

  follow (rel, data) {
    if (this.matches(rel)) {
      return jsonPointer.get(data, rel)
    }

    return []
  }

  update (rel, obj, data) {
    return jsonPointer.set(obj, rel, data)
  }

}

delete new PointerRefSpec() // register spec with global pool by immediately invoking it

export const pointer = (path, value) => new PointerRef(path, value)

export default { PointerRef, PointerRefSpec, pointer }

import jsonQuery from 'json-query'
import { AbstractRef, AbstractRefSpec } from './abstract'

export class QueryRef extends AbstractRef {

  constructor (path, value) {
    super({ path, value, spec: new QueryRefSpec() })
  }
}

export class QueryRefSpec extends AbstractRefSpec {

  constructor () {
    super('json-query', 'https://www.npmjs.com/package/json-query')
  }

  matches (rel) {
    if (!rel || (typeof rel !== 'string' && !Array.isArray(rel))) {
      return false
    }

    // FIXME: Use a RegEx instead, not sufficient
    return jsonQuery(rel).key !== undefined
  }

  follow (rel, data, locals) {
    if (this.matches(rel)) {
      return jsonQuery(rel, { data, locals }).value || []
    }
  }

  update (rel, obj, data) {
    // TODO: No core support, so add it here
    console.warn('json-query does not support updating values!')
  }

}

delete new QueryRefSpec() // register spec with global pool by immediately invoking it

export const query = (path, value) => new QueryRef(path, value)

export default { QueryRef, QueryRefSpec, query }
